def get_season():
    """
    Определяет текущее время года на основе текущего месяца
    
    Returns:
        str: Время года ('зима', 'весна', 'лето', 'осень')
    """
    from datetime import datetime
    
    current_month = datetime.now().month
    
    if current_month in [12, 1, 2]:
        return 'зима'
    elif current_month in [3, 4, 5]:
        return 'весна'
    elif current_month in [6, 7, 8]:
        return 'лето'
    else:  # 9, 10, 11
        return 'осень'

def get_time_of_day(timezone_offset=None):
    """
    Определяет текущее время суток на основе текущего часа

    Args:
        timezone_offset (int, optional): Смещение часового пояса в секундах от UTC

    Returns:
        str: Время суток ('утро', 'день', 'вечер', 'ночь')
    """
    from datetime import datetime, timezone, timedelta

    if timezone_offset is not None:
        # Получаем UTC время и добавляем смещение
        utc_time = datetime.now(timezone.utc)
        local_time = utc_time + timedelta(seconds=timezone_offset)
        current_hour = local_time.hour
    else:
        # Используем локальное время сервера (по умолчанию)
        current_hour = datetime.now().hour

    if 6 <= current_hour < 12:
        return 'утро'
    elif 12 <= current_hour < 18:
        return 'день'
    elif 18 <= current_hour < 24:
        return 'вечер'
    else:  # 0 <= current_hour < 6
        return 'ночь'

def get_local_time_str(timezone_offset):
    """
    Возвращает локальное время города в формате HH:MM

    Args:
        timezone_offset (int): Смещение часового пояса в секундах от UTC

    Returns:
        str: Время в формате HH:MM
    """
    from datetime import datetime, timezone, timedelta

    utc_time = datetime.now(timezone.utc)
    local_time = utc_time + timedelta(seconds=timezone_offset)
    return local_time.strftime('%H:%M')

def is_valid_combination(temp_range, condition, season):
    """
    Проверяет, является ли комбинация температуры, погоды и сезона реалистичной
    
    Args:
        temp_range (str): Диапазон температуры
        condition (str): Погодное условие
        season (str): Время года
        
    Returns:
        bool: True если комбинация реалистична, False если нет
    """
    # Нелогичные сезонные температуры
    if season == 'лето' and temp_range in ['ниже -40', '-40 до -31', '-30 до -21', '-20 до -11', '-10 до -1']:
        return False
    if season == 'зима' and temp_range in ['+11 до +20', '+21 до +30', '+31 до +40', 'выше +40']:
        return False
    if season in ['весна', 'осень'] and temp_range in ['ниже -40', '-40 до -31', '-30 до -21', '+31 до +40', 'выше +40']:
        return False
    
    # Нелогичные комбинации погоды и температуры
    if condition == 'дождь' and temp_range in ['ниже -40', '-40 до -31', '-30 до -21', '-20 до -11', '-10 до -1']:
        return False
    if condition == 'гроза' and temp_range in ['ниже -40', '-40 до -31', '-30 до -21', '-20 до -11', '-10 до -1']:
        return False
    if condition == 'снег' and temp_range in ['+11 до +20', '+21 до +30', '+31 до +40', 'выше +40']:
        return False
    
    return True

def get_clothing_advice(temp, weather_condition, season=None, time_of_day=None, wind_speed=0, timezone_offset=None):
    """
    Возвращает совет по одежде, учитывая все параметры погоды,
    а также добавляет информацию о силе ветра в конце.

    Args:
        temp (float): Температура в градусах Цельсия
        weather_condition (str): Описание погоды
        season (str, optional): Время года ('зима', 'весна', 'лето', 'осень').
                               Если None, определяется автоматически.
        time_of_day (str, optional): Время суток ('утро', 'день', 'вечер', 'ночь').
                                    Если None, определяется автоматически.
        wind_speed (float, optional): Скорость ветра в м/с. По умолчанию 0.
        timezone_offset (int, optional): Смещение часового пояса в секундах от UTC.

    Returns:
        str: Комбинированный совет по одежде и информация о ветре
    """
    # Нормализуем погодные условия
    weather_condition = weather_condition.lower()

    # Определяем время года и время суток, если не указаны
    if season is None:
        season = get_season()
    else:
        season = season.lower()

    if time_of_day is None:
        time_of_day = get_time_of_day(timezone_offset)
    else:
        time_of_day = time_of_day.lower()
    
    # Определяем диапазон температуры
    if temp < -40:
        temp_range = "ниже -40"
    elif -40 <= temp < -31:
        temp_range = "-40 до -31"
    elif -30 <= temp < -21:
        temp_range = "-30 до -21"
    elif -20 <= temp < -11:
        temp_range = "-20 до -11"
    elif -10 <= temp < -1:
        temp_range = "-10 до -1"
    elif 0 <= temp <= 10:
        temp_range = "0 до +10"
    elif 11 <= temp <= 20:
        temp_range = "+11 до +20"
    elif 21 <= temp <= 30:
        temp_range = "+21 до +30"
    elif 31 <= temp <= 40:
        temp_range = "+31 до +40"
    else:
        temp_range = "выше +40"
    
    # Определяем основное погодное условие
    if any(word in weather_condition for word in ['ясно', 'чистое небо', 'безоблачно']):
        condition = "ясно"
    elif any(word in weather_condition for word in ['облачно с прояснениями', 'переменная облачность']):
        condition = "облачно"
    elif any(word in weather_condition for word in ['пасмурно', 'морось']):
        condition = "пасмурно"
    elif any(word in weather_condition for word in ['дождь', 'ливень']):
        condition = "дождь"
    elif any(word in weather_condition for word in ['гроза']):
        condition = "гроза"
    elif any(word in weather_condition for word in ['снег', 'снегопад']):
        condition = "снег"
    elif any(word in weather_condition for word in ['туман', 'мгла', 'дымка']):
        condition = "туман"
    else:
        condition = "облачно"  # Значение по умолчанию
    
    # Проверяем, является ли комбинация реалистичной
    if not is_valid_combination(temp_range, condition, season):
        # Если комбинация нереалистична, подбираем ближайшую реалистичную
        if condition == 'дождь' and temp < 0:
            condition = 'снег'  # Заменяем дождь на снег при минусовой температуре
        elif condition == 'гроза' and temp < 0:
            condition = 'снег'  # Заменяем грозу на снег при минусовой температуре
        elif condition == 'снег' and temp > 5:
            condition = 'дождь'  # Заменяем снег на дождь при высокой температуре
        
        # Корректируем диапазон температуры для сезона, если необходимо
        if season == 'лето' and temp < 0:
            temp_range = "0 до +10"  # Минимальная реалистичная температура для лета
        elif season == 'зима' and temp > 10:
            temp_range = "0 до +10"  # Максимальная реалистичная температура для зимы
        elif season in ['весна', 'осень'] and (temp < -30 or temp > 30):
            if temp < -30:
                temp_range = "-20 до -11"  # Минимальная реалистичная температура для весны/осени
            else:
                temp_range = "+21 до +30"  # Максимальная реалистичная температура для весны/осени
    
    # Получаем совет по одежде из базы советов
    advice = get_specific_advice(temp_range, condition, season, time_of_day)
    
    # Получаем информацию о ветре
    wind_name, wind_range, wind_description = get_wind_description(wind_speed)
    
    # Формируем полный совет с информацией о ветре
    full_advice = f"{advice} Ветер: {wind_name} ({wind_range}) — {wind_description}"
    
    return full_advice

